mapper.select:
select count(id)
from clock_in
where student_id = #{studentId}
  and card_time = #{date}

select student_id studentId
from clock_in
where student_id in (

    #{studentId}

)

select s.account, s.id studentId, s.student_name studentName, c.card_days cardDays, se.phase, sum(d.online_time) onlineTime from clock_in c join student s on c.student_id = s.id join student_expansion se on s.id = se.student_id join duration d on s.id = d.student_id and to_days(d.login_time) = to_days(now()) and s.account in (
#{c}
) and to_days(c.card_time) = to_days(#{date}) group by s.id
