    and rw.read_type_id = #{readTypeId}

select count(*)
from read_word rw
where rw.student_id = #{studentId}
  and rw.course_id = #{courseId}
  and rw.memory_strength < 1
  and rw.push <= now()
  and rw.type = #{type}

select

from read_word rw
