mapper.select:
select hours.type,hours.create_time time,stu.account,stu.student_name name from student_hours hours left join student stu on stu.id=hours.student_id
where hours.admin_id=#{adminId}
order by hours.create_time desc

select
from student_hours where to_days(create_time)=to_days(#{time})
and admin_id=#{adminId}
