mapper.delete:
delete
from study_capacity
where student_id = #{studentId}
  and course_id = #{courseId}
  and type in (20, 21, 22)

mapper.select:
select

from study_capacity
where student_id = #{dto.studentId}
and unit_id = #{dto.unitId}
and type = #{dto.type}
and memory_strength < 1
and push <= now()
order by push limit 1

select

from study_capacity
where student_id = #{dto.studentId}
and unit_id = #{dto.unitId}
and type = #{dto.type}
and memory_strength < 1
order by push limit 1

select

from study_capacity
where student_id = #{learn.studentId}
and unit_id = #{learn.unitId}
and word_id = #{learn.vocabularyId}
and type = #{type}
order by push
limit 1
