mapper.delete:
delete
from study_capacity
where student_id = #{studentId}
  and course_id = #{courseId}
  and type in (20, 21, 22)

mapper.select:
select

from study_capacity
where student_id = #{dto.studentId}
and unit_id = #{dto.unitId}
and type = #{dto.type}
and memory_strength < 1
and push <= now()
order by push limit 1

select

from study_capacity
where student_id = #{dto.studentId}
and unit_id = #{dto.unitId}
and type = #{dto.type}
and memory_strength < 1
order by push limit 1

select

from study_capacity
where student_id = #{learn.studentId}
and unit_id = #{learn.unitId}
and word_id = #{extend.wordId}
and type = #{type}
order by push
limit 1

select

from vocabulary a
INNER JOIN study_capacity sc
on a.id = sc.word_id
inner join unit_vocabulary_new voNew
on voNew.vocabulary_id=a.id
inner join learn_new leaNew
on leaNew.unit_id=voNew.unit_id
where
sc.unit_id = #{unitId}
and sc.student_id = #{studentId}
and voNew.group=#{group}
and sc.push < #{dateTime}
and leaNew.easy_or_hard=#{easyOrHard}
and sc.memory_strength < 1
and sc.type=#{type}
and a.delStatus = 1
ORDER BY sc.push
LIMIT 0,1

select

from study_capacity
where student_id=#{studentId}
and unit_id =#{unitId}
and word_id=#{wordId}
and `type`=#{type}

mapper.delete:
delete from study_capacity where
 student_id=#{studentId}
and unit_id =#{unitId}
and word_id=#{wordId}
 and `type`=#{type}
