mapper.select:
select u.id id,count(t.id) number , u.unit_name unitName,u.course_id courseId,course.label label,course.grade grade,course.version version from unit u left join test_record t on u.id=t.unit_id join student_unit su on su.unit_id =u.id join course course on course.id = u.course_id where su.student_id = #{studentId} and su.type = 1 and u.course_id in (
#{courseId}
) group by u.id order by u.course_id asc, u.id asc

select distinct course.version version from unit u left join test_record t on u.id=t.unit_id join student_unit su on su.unit_id =u.id join course course on course.id = u.course_id where su.student_id = #{studentId} and su.type = 1 and u.course_id in (
#{courseId}
) group by u.id order by u.course_id asc, u.id asc

select DISTINCT voice.word_id wordId ,teks.id,teks.sentence,teks.paraphrase,voice.score,voice.voice_url url ,voice.create_time time
from teks teks
join voice voice on teks.id= voice.word_id
where teks.unit_id=#{unitId}
and voice.student_id= #{studentId}
and count= #{count}
GROUP BY voice.word_id
ORDER BY voice.create_time,teks.id desc

select max(count) count
from teks teks
join voice voice on teks.id= voice.word_id
where teks.unit_id=#{unitId}
and voice.student_id= #{studentId}
