mapper.select:
select word_chinese
from unit_vocabulary_new
where vocabulary_id <> #{wordId}
  and unit_id = #{unitId}
  and word_chinese <> #{chinese}
limit 0,3

select count(v.id)
from vocabulary v,
     unit_vocabulary_new uv
where uv.unit_id = #{unitId}
  and uv.vocabulary_id = v.id
  and uv.group = #{group}
  and (v.small_picture_url is not null
    or v.middle_picture_url is not null
    or v.high_picture_url is not null)

SELECT
vocabulary_id id,
word_chinese wordChinese
FROM
unit_vocabulary_new
WHERE
unit_id = #{unitId}
and vocabulary_id in (

    #{id}

)

SELECT vocabulary_id id, word_chinese wordChinese FROM unit_vocabulary_new WHERE unit_id IN ( SELECT id FROM unit_new WHERE course_id = #{courseId} ) AND vocabulary_id IN (
#{id}
)

SELECT vocabulary_id id, word_chinese wordChinese FROM unit_vocabulary_new WHERE unit_id IN ( SELECT b.id FROM course_new a JOIN unit_new b ON a.id = b.course_id AND a.version like '${start}%' AND a.version like '%${end}' ) AND vocabulary_id IN (
#{id}
)

select unit_id unitId, count(vocabulary_id) count from unit_vocabulary_new where unit_id in (

    #{unitId}

)
group by unit_id
