	#{id}

)

select

from

where student_id = #{student.id}

    and course_id = #{courseId}


    and unit_id = #{unitId}

and vocabulary_id =#{id} group by vocabulary_id

select
from , vocabulary v where cr.vocabulary_id = v.id and v.delStatus = 1 and cr.memory_strength < 1 and cr.student_id = #{student.id} and cr.course_id = #{courseId}
and cr.unit_id = #{unitId}
order by cr.push asc

select count(distinct cr.vocabulary_id) from , vocabulary v where cr.vocabulary_id = v.id and v.delStatus = 1 and cr.memory_strength < 1 and cr.student_id = #{student.id} and cr.course_id = #{courseId} and cr.push < now()
and cr.unit_id = #{unitId}
order by push asc
