mapper.select:
select study_count from learn where student_id = #{studentId}

    and course_id = #{courseId}


    and unit_id = #{unitId}

and learn_count = #{count}
and study_model = #{studyModel}
 and

    vocabulary_id = #{id}
    GROUP BY vocabulary_id


    example_id = #{id}
    GROUP BY example_id

select

from learn where student_id=#{studentId} and
unit_id=#{unitId}

    and vocabulary_id=#{wordId}


    and example_id = #{sentenceId}

and learn_count = #{count}
and
study_model=#{studyModel}

select vocabulary_id
from learn
where
    student_id = #{studentId} and unit_id = #{unitId}
order by learn_time desc
limit 0, #{wordCount}

select

from learn where student_id=#{studentId} and
unit_id=#{learn.unitId}

    and vocabulary_id=#{learn.vocabularyId}


    and example_id=#{learn.exampleId}

and learn_count = #{count}
and
study_model=#{studyModel}

select

from learn
where id = #{id,jdbcType=BIGINT}

select count(id)
from learn
where student_id = #{studentId}
  and course_id = #{courseId}
  and study_model = #{studyModel}
  and type = 1

select count(distinct study_model)
from learn
where student_id = #{studentId} and unit_id = #{unitId}

select count(id)
from learn
where student_id = #{studentId}
  and to_days(update_time) = to_days(now())
  and unit_id = #{unitId}
  and study_model = #{studyModel}
  and type = 1

select max(learn_time)
from learn
where student_id = #{stuId}

SELECT course_id id, first_study_time value FROM learn WHERE first_study_time IS NOT NULL AND student_id = #{stuId} AND course_id in (
#{course.id}
)

SELECT
course_id id,
max(learn_time) value
FROM
learn
WHERE
student_id = #{stuId}
AND course_id in (

    #{course.id}

)

