mapper.select:
		SELECT
			count(id) AS count,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '单词辨音'
			    and type = 1
			) AS a,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '词组辨音'
                and type = 1
			) AS b,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '快速单词'
                and type = 1
			) AS c,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '快速词组'
                and type = 1
			) AS d,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '词汇考点'
                and type = 1
			) AS e,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '快速句型'
                and type = 1
			) AS f,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '语法辨析'
                and type = 1
			) AS g,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '单词默写'
                and type = 1
			) AS h,
			(
				SELECT
					count(id)
				FROM
					learn
				WHERE
					date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
				AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
				AND student_id = #{studentId}
				AND study_model = '词组默写'
                and type = 1
        ) AS i,
        (
        select count(id) from learn where date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
        AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
        AND student_id = #{studentId} and study_model in ('单词图鉴','慧记忆','慧听写','慧默写') and type = 1
        ) word,
        (
        select count(id) from learn where date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
        AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
        AND student_id = #{studentId} and study_model in ('例句听力','例句默写','例句翻译') and type = 1
        ) sentence,
        (
        select count(id) from test_record where student_id = #{studentId} and genre = '课文测试'
        and date_format(test_start_time, '%Y-%m-%d') >= #{weekStart}
        AND date_format(test_start_time, '%Y-%m-%d') <= #{weekEnd}
        ) text
		FROM
			learn
		WHERE
			date_format(learn_time, '%Y-%m-%d') >= #{weekStart}
		AND date_format(learn_time, '%Y-%m-%d') <= #{weekEnd}
		AND student_id = #{studentId}
        and type = 1

select COUNT(id) AS count, sum(id) AS sum from learn where student_id = #{studentId} AND course_id =
#{course_id}


AND unit_id =
(select MAX(unit_id) from learn where student_id = #{studentId} AND course_id = #{course_id}

) GROUP BY id

select count(id) from learn l where l.student_id=#{studentId} and l.unit_id=#{unitId} and l.study_model = #{studyModel}
and l.vocabulary_id is not null and (select v.delStatus from vocabulary v where v.id = l.vocabulary_id) = 1
and example_id is not null

select count(l.id) from learn l, vocabulary v
where v.id = l.vocabulary_id
and v.delStatus = 1
and l.student_id=#{studentId}
and l.study_model = #{typeStr}
and l.type = 1

    and l.course_id in (

        #{id}

    )

select count(l.id) from vocabulary v, learn l
where v.id = l.vocabulary_id
and v.delStatus = 1
and l.student_id = #{studentId}
and l.study_model = #{typeStr}
and l.type = 1

    and course_id in (

        #{id}

    )

and l.status = 0

